import React, { useRef } from 'react';

const SimpleSQLEditor = ({ 
  value, 
  onChange, 
  onExecute, 
  isExecuting, 
  height = '500px',
  readOnly = false
}) => {
  const textareaRef = useRef(null);

  const handleKeyDown = (e) => {
    // Handle Ctrl+Enter for execution
    if ((e.ctrlKey || e.metaKey) && e.key === 'Enter') {
      e.preventDefault();
      if (onExecute && !isExecuting) {
        onExecute();
      }
    }

    // Handle Tab for indentation
    if (e.key === 'Tab') {
      e.preventDefault();
      const textarea = e.target;
      const start = textarea.selectionStart;
      const end = textarea.selectionEnd;

      // Insert tab character
      const newValue = value.substring(0, start) + '  ' + value.substring(end);
      onChange(newValue);

      // Set cursor position after the inserted spaces
      setTimeout(() => {
        textarea.selectionStart = textarea.selectionEnd = start + 2;
      }, 0);
    }
  };

  const handleChange = (e) => {
    onChange(e.target.value);
  };

  return (
    <div className="relative w-full" style={{ height }}>
      <textarea
        ref={textareaRef}
        value={value}
        onChange={handleChange}
        onKeyDown={handleKeyDown}
        readOnly={readOnly}
        className="w-full h-full p-4 bg-gray-900 text-white font-mono text-sm border-0 resize-none focus:outline-none focus:ring-2 focus:ring-blue-500"
        style={{
          backgroundColor: '#1e1e1e',
          color: '#d4d4d4',
          fontFamily: 'Monaco, Menlo, "Ubuntu Mono", monospace',
          fontSize: '14px',
          lineHeight: '1.5',
          tabSize: 2,
          whiteSpace: 'pre'
        }}
        placeholder="-- Write your SQL query here
-- Use Ctrl+Enter to execute
-- Available keywords: SELECT, FROM, WHERE, JOIN, GROUP BY, ORDER BY"
        spellCheck={false}
        autoCapitalize="off"
        autoComplete="off"
        autoCorrect="off"
      />

      {/* Help text overlay */}
      <div className="absolute top-2 right-2 text-xs text-gray-400 bg-gray-800 px-2 py-1 rounded opacity-75 pointer-events-none">
        SQL Editor â€¢ Ctrl+Enter to run
      </div>
    </div>
  );
};

export default SimpleSQLEditor;
