import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import BookmarksList from '../components/BookmarksList';

const BookmarksPage = () => {
  const [activeTab, setActiveTab] = useState('all');
  const [stats, setStats] = useState({ favorite: 0, review_later: 0, challenging: 0, total: 0 });
  const [loading, setLoading] = useState(true);

  const tabs = [
    { key: 'all', label: '📝 All Bookmarks', description: 'View all saved problems', count: stats.total },
    { key: 'favorites', label: '⭐ Favorites', description: 'Your favorite problems', count: stats.favorite },
    { key: 'review', label: '📚 Review Later', description: 'Problems to review', count: stats.review_later },
    { key: 'challenging', label: '🔥 Challenging', description: 'Difficult problems', count: stats.challenging }
  ];

  // Session ID for anonymous users
  const getSessionId = () => {
    let sessionId = localStorage.getItem('sql_practice_session_id');
    if (!sessionId) {
      sessionId = `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
      localStorage.setItem('sql_practice_session_id', sessionId);
    }
    return sessionId;
  };

  useEffect(() => {
    fetchStats();
  }, []);

  const fetchStats = async () => {
    try {
      const sessionId = getSessionId();
      const response = await fetch('http://localhost:5001/api/bookmarks/stats', {
        headers: { 'X-Session-ID': sessionId }
      });

      if (response.ok) {
        const data = await response.json();
        setStats(data.stats || { favorite: 0, review_later: 0, challenging: 0, total: 0 });
      }
    } catch (error) {
      console.error('Error fetching bookmark stats:', error);
    } finally {
      setLoading(false);
    }
  };

  const getBookmarkTypeFromTab = (tab) => {
    switch (tab) {
      case 'favorites': return 'favorite';
      case 'review': return 'review_later';
      case 'challenging': return 'challenging';
      case 'all': return null;
      default: return null;
    }
  };

  return (
    <div className="max-w-7xl mx-auto px-4 py-8 bg-gray-50 dark:bg-gray-900 min-h-screen transition-colors">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-4xl font-bold text-gray-900 dark:text-white bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent animate-slide-up">
          📚 My Bookmarks
        </h1>
        <p className="text-gray-600 dark:text-gray-400 mt-2 animate-slide-up animation-delay-200">
          Manage your saved SQL problems and track your progress
        </p>
      </div>

      {/* Tab Navigation */}
      <div className="bg-white dark:bg-gray-800 rounded-xl shadow-lg border border-gray-200 dark:border-gray-700 mb-6 animate-slide-up animation-delay-300">
        <div className="p-6 border-b border-gray-200 dark:border-gray-700">
          <div className="flex flex-wrap gap-2">
            {tabs.map(tab => (
              <button
                key={tab.key}
                onClick={() => setActiveTab(tab.key)}
                className={`
                  px-4 py-2 rounded-lg text-sm font-medium transition-all duration-200
                  ${activeTab === tab.key
                    ? 'bg-blue-100 dark:bg-blue-900/50 text-blue-700 dark:text-blue-300 shadow-sm'
                    : 'text-gray-600 dark:text-gray-400 hover:text-gray-800 dark:hover:text-gray-200 hover:bg-gray-100 dark:hover:bg-gray-700'
                  }
                `}
                title={tab.description}
              >
                {tab.label}
              </button>
            ))}
          </div>
        </div>

        {/* Tab Content */}
        <div className="p-6">
          {activeTab === 'overview' ? (
            <div className="space-y-6">
              <BookmarkStats />

              {/* Quick Actions */}
              <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-6">
                <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
                  🚀 Quick Actions
                </h3>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <button
                    onClick={() => setActiveTab('favorites')}
                    className="p-4 bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-600 rounded-lg hover:shadow-md transition-shadow text-left"
                  >
                    <div className="text-2xl mb-2">⭐</div>
                    <div className="font-medium text-gray-900 dark:text-white">Review Favorites</div>
                    <div className="text-sm text-gray-500 dark:text-gray-400">Revisit your best problems</div>
                  </button>

                  <button
                    onClick={() => setActiveTab('challenging')}
                    className="p-4 bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-600 rounded-lg hover:shadow-md transition-shadow text-left"
                  >
                    <div className="text-2xl mb-2">🔥</div>
                    <div className="font-medium text-gray-900 dark:text-white">Practice Hard Problems</div>
                    <div className="text-sm text-gray-500 dark:text-gray-400">Challenge yourself</div>
                  </button>

                  <button
                    onClick={() => setActiveTab('review')}
                    className="p-4 bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-600 rounded-lg hover:shadow-md transition-shadow text-left"
                  >
                    <div className="text-2xl mb-2">📚</div>
                    <div className="font-medium text-gray-900 dark:text-white">Study Queue</div>
                    <div className="text-sm text-gray-500 dark:text-gray-400">Review saved problems</div>
                  </button>
                </div>
              </div>
            </div>
          ) : (
            <BookmarksList 
              bookmarkType={getBookmarkTypeFromTab(activeTab)}
              className="animate-fade-in"
            />
          )}
        </div>
      </div>

      {/* Help Section */}
      <div className="bg-blue-50 dark:bg-blue-900/20 border border-blue-200 dark:border-blue-800 rounded-lg p-6 animate-slide-up animation-delay-500">
        <h3 className="text-lg font-semibold text-blue-900 dark:text-blue-300 mb-2">
          💡 Bookmark Tips
        </h3>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm text-blue-800 dark:text-blue-300">
          <div>
            <strong>⭐ Favorites:</strong> Problems you enjoyed or found particularly useful for learning
          </div>
          <div>
            <strong>📚 Review Later:</strong> Problems to revisit when you have more time or want to practice
          </div>
          <div>
            <strong>🔥 Challenging:</strong> Difficult problems that pushed your skills to the limit
          </div>
        </div>
      </div>
    </div>
  );
};

export default BookmarksPage;
