import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import BookmarkButton from '../components/BookmarkButton';

function ProblemsPage() {
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);

  // Map category slugs to display names
  const categorySlugToName = {
    'a/b-testing': 'A/B Testing',
    'advanced-topics': 'Advanced Topics',
    'aggregation': 'Aggregation',
    'basic-queries': 'Basic Queries',
    'energy-analytics': 'Energy Analytics',
    'fraud-detection': 'Fraud Detection',
    'joins': 'Joins',
    'recommendation-systems': 'Recommendation Systems',
    'subqueries': 'Subqueries',
    'supply-chain': 'Supply Chain',
    'time-analysis': 'Time Analysis',
    'window-functions': 'Window Functions'
  };

  const categoryParam = searchParams.get('category');
  const initialCategory = categoryParam && categorySlugToName[categoryParam] ? categorySlugToName[categoryParam] : 'all';

  const [selectedDifficulty, setSelectedDifficulty] = useState(searchParams.get('difficulty') || 'all');
  const [selectedCategory, setSelectedCategory] = useState(initialCategory);
  const [selectedCompany, setSelectedCompany] = useState(searchParams.get('company') || 'all');
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [problems, setProblems] = useState([]);
  const [companies, setCompanies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchProblems = async () => {
    setLoading(true);
    try {
      const params = new URLSearchParams();
      if (selectedDifficulty !== 'all') params.append('difficulty', selectedDifficulty);

      // Convert category display name back to slug for API call
      if (selectedCategory !== 'all') {
        const categoryNameToSlug = {
          'A/B Testing': 'a/b-testing',
          'Advanced Topics': 'advanced-topics',
          'Aggregation': 'aggregation',
          'Basic Queries': 'basic-queries',
          'Energy Analytics': 'energy-analytics',
          'Fraud Detection': 'fraud-detection',
          'Joins': 'joins',
          'Recommendation Systems': 'recommendation-systems',
          'Subqueries': 'subqueries',
          'Supply Chain': 'supply-chain',
          'Time Analysis': 'time-analysis',
          'Window Functions': 'window-functions'
        };
        const categorySlug = categoryNameToSlug[selectedCategory] || selectedCategory;
        params.append('category', categorySlug);
      }

      if (selectedCompany !== 'all') params.append('company', selectedCompany);

      const response = await fetch(`http://localhost:5001/api/sql/problems?${params}`);
      const data = await response.json();

      if (response.ok) {
        // Sort problems by numeric_id and format for display
        const sortedProblems = data.problems
          .sort((a, b) => (a.numeric_id || 0) - (b.numeric_id || 0))
          .map(p => ({
            ...p,
            category: p.category_name,
            acceptance: `${p.acceptance_rate}%`,
            solved: Math.random() > 0.7 // Random solved status for demo
          }));
        setProblems(sortedProblems);
      } else {
        setError(data.error || 'Failed to fetch problems');
      }
    } catch (err) {
      setError('Failed to connect to server');
    } finally {
      setLoading(false);
    }
  };

  // Load companies on component mount
  useEffect(() => {
    const fetchCompanies = async () => {
      try {
        const response = await fetch('http://localhost:5001/api/sql/companies');
        const data = await response.json();
        if (response.ok) {
          setCompanies([
            { name: 'all', display: 'All Companies', count: 0 },
            ...data
          ]);
        }
      } catch (err) {
        console.error('Failed to fetch companies:', err);
      }
    };

    fetchCompanies();
  }, []);

  useEffect(() => {
    fetchProblems();
  }, [selectedDifficulty, selectedCategory, selectedCompany]);

  const categories = ["all", "A/B Testing", "Advanced Topics", "Aggregation", "Basic Queries", "Energy Analytics", "Fraud Detection", "Joins", "Recommendation Systems", "Subqueries", "Supply Chain", "Time Analysis", "Window Functions"];
  const difficulties = ["all", "easy", "medium", "hard"];

  const filteredProblems = problems.filter(problem => {
    // Basic filters
    if (selectedDifficulty !== 'all' && problem.difficulty !== selectedDifficulty) return false;
    if (selectedCategory !== 'all' && problem.category !== selectedCategory) return false;
    if (selectedCompany !== 'all' && problem.company !== selectedCompany) return false;

    // Status filter
    if (statusFilter === 'solved' && !problem.solved) return false;
    if (statusFilter === 'unsolved' && problem.solved) return false;

    // Search term filter
    if (searchTerm) {
      const searchLower = searchTerm.toLowerCase();
      return problem.title.toLowerCase().includes(searchLower) ||
             (problem.category && problem.category.toLowerCase().includes(searchLower)) ||
             (problem.company && problem.company.toLowerCase().includes(searchLower)) ||
             (problem.numeric_id && problem.numeric_id.toString().includes(searchTerm));
    }

    return true;
  });

  const getDifficultyColor = (difficulty) => {
    switch(difficulty) {
      case 'easy': return 'text-green-600';
      case 'medium': return 'text-yellow-600';
      case 'hard': return 'text-red-600';
      default: return 'text-gray-600';
    }
  };

  const getDifficultyIcon = (difficulty) => {
    switch(difficulty) {
      case 'easy': return '●';
      case 'medium': return '●';
      case 'hard': return '●';
      default: return '●';
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-white dark:bg-gray-900">
        <div className="max-w-7xl mx-auto px-4 py-8">
          <div className="text-center py-12">
            <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
            <div className="text-gray-500 dark:text-gray-400 mt-2">Loading problems...</div>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-white dark:bg-gray-900">
        <div className="max-w-7xl mx-auto px-4 py-8">
          <div className="bg-red-50 dark:bg-red-900/50 border border-red-200 dark:border-red-800 rounded-md p-4">
            <div className="text-red-800 dark:text-red-300 font-medium">Error</div>
            <div className="text-red-600 dark:text-red-400 text-sm mt-1">{error}</div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-white dark:bg-gray-900">
      <div className="max-w-7xl mx-auto px-4 py-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-2xl font-bold text-gray-900 dark:text-white mb-2">
            Problems
          </h1>
          <p className="text-gray-600 dark:text-gray-400">
            Solve SQL problems to improve your skills
          </p>
        </div>

        {/* Filters */}
        <div className="bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-700 rounded-lg mb-6">
          <div className="p-4 border-b border-gray-200 dark:border-gray-700">
            <div className="flex flex-wrap gap-4">
              {/* Status Filter */}
              <select
                value={statusFilter}
                onChange={(e) => setStatusFilter(e.target.value)}
                className="border border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-700 text-gray-900 dark:text-white rounded px-3 py-2 text-sm"
              >
                <option value="all">All</option>
                <option value="solved">Solved</option>
                <option value="unsolved">Todo</option>
              </select>

              {/* Difficulty Filter */}
              <select
                value={selectedDifficulty}
                onChange={(e) => setSelectedDifficulty(e.target.value)}
                className="border border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-700 text-gray-900 dark:text-white rounded px-3 py-2 text-sm"
              >
                <option value="all">Difficulty</option>
                {difficulties.slice(1).map(diff => (
                  <option key={diff} value={diff}>
                    {diff.charAt(0).toUpperCase() + diff.slice(1)}
                  </option>
                ))}
              </select>

              {/* Category Filter */}
              <select
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
                className="border border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-700 text-gray-900 dark:text-white rounded px-3 py-2 text-sm"
              >
                <option value="all">All Topics</option>
                {categories.slice(1).map(cat => (
                  <option key={cat} value={cat}>{cat}</option>
                ))}
              </select>

              {/* Search */}
              <input
                type="text"
                placeholder="Search problems..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="border border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-700 text-gray-900 dark:text-white rounded px-3 py-2 text-sm flex-1 min-w-0"
              />
            </div>
          </div>
        </div>

        {/* Problems Table - LeetCode Style */}
        <div className="bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-700 rounded-lg overflow-hidden">
          {/* Table Header */}
          <div className="bg-gray-50 dark:bg-gray-900 border-b border-gray-200 dark:border-gray-700">
            <div className="grid grid-cols-12 gap-4 px-4 py-3 text-sm font-medium text-gray-700 dark:text-gray-300">
              <div className="col-span-1">Status</div>
              <div className="col-span-5">Title</div>
              <div className="col-span-2">Acceptance</div>
              <div className="col-span-2">Difficulty</div>
              <div className="col-span-2">Company</div>
            </div>
          </div>

          {/* Table Body */}
          <div className="divide-y divide-gray-200 dark:divide-gray-700">
            {filteredProblems.map((problem) => (
              <div key={problem.id} className="grid grid-cols-12 gap-4 px-4 py-3 hover:bg-gray-50 dark:hover:bg-gray-750 transition-colors">
                {/* Status */}
                <div className="col-span-1 flex items-center">
                  {problem.solved ? (
                    <div className="w-4 h-4 bg-green-500 rounded-full flex items-center justify-center">
                      <svg className="w-2.5 h-2.5 text-white" fill="currentColor" viewBox="0 0 20 20">
                        <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                      </svg>
                    </div>
                  ) : (
                    <div className="w-4 h-4 border-2 border-gray-300 dark:border-gray-600 rounded-full"></div>
                  )}
                </div>

                {/* Title */}
                <div className="col-span-5 flex items-center">
                  <span className="text-blue-600 dark:text-blue-400 font-medium mr-2">
                    {problem.numeric_id}.
                  </span>
                  <Link
                    to={`/practice/${problem.numeric_id}`}
                    className="text-gray-900 dark:text-white hover:text-blue-600 dark:hover:text-blue-400 transition-colors"
                  >
                    {problem.title}
                  </Link>
                </div>

                {/* Acceptance */}
                <div className="col-span-2 flex items-center text-gray-600 dark:text-gray-400">
                  {problem.acceptance}
                </div>

                {/* Difficulty */}
                <div className="col-span-2 flex items-center">
                  <span className={`${getDifficultyColor(problem.difficulty)} font-medium`}>
                    {getDifficultyIcon(problem.difficulty)} {problem.difficulty.charAt(0).toUpperCase() + problem.difficulty.slice(1)}
                  </span>
                </div>

                {/* Company */}
                <div className="col-span-2 flex items-center justify-between">
                  <span className="text-gray-600 dark:text-gray-400 text-sm">
                    {problem.company || '-'}
                  </span>
                  <BookmarkButton problemId={problem.numeric_id} size="sm" />
                </div>
              </div>
            ))}
          </div>

          {/* Empty State */}
          {filteredProblems.length === 0 && (
            <div className="text-center py-12">
              <div className="text-gray-500 dark:text-gray-400">
                No problems found matching your criteria
              </div>
            </div>
          )}
        </div>

        {/* Results Summary */}
        <div className="mt-4 text-sm text-gray-600 dark:text-gray-400 text-center">
          Showing {filteredProblems.length} of {problems.length} problems
        </div>
      </div>
    </div>
  );
}

export default ProblemsPage;
