import React, { useEffect, useRef } from 'react';

const SQLHighlightDisplay = ({ 
  value, 
  height = 'auto',
  className = ''
}) => {
  const containerRef = useRef(null);
  const highlightRef = useRef(null);
  const textareaRef = useRef(null);

  // Simple SQL highlighting function that returns HTML string
  const highlightSQL = (text) => {
    if (!text) return '';

    // Define SQL keywords
    const keywords = [
      'SELECT', 'FROM', 'WHERE', 'JOIN', 'INNER', 'LEFT', 'RIGHT', 'FULL', 'OUTER',
      'ON', 'GROUP', 'BY', 'HAVING', 'ORDER', 'LIMIT', 'OFFSET', 'DISTINCT',
      'COUNT', 'SUM', 'AVG', 'MIN', 'MAX', 'AS', 'AND', 'OR', 'NOT', 'IN', 'LIKE',
      'BETWEEN', 'IS', 'NULL', 'EXISTS', 'CASE', 'WHEN', 'THEN', 'ELSE', 'END',
      'UNION', 'ALL', 'INSERT', 'INTO', 'VALUES', 'UPDATE', 'SET', 'DELETE',
      'CREATE', 'ALTER', 'DROP', 'TABLE', 'INDEX', 'PRIMARY', 'KEY', 'FOREIGN',
      'REFERENCES', 'CONSTRAINT', 'CHECK', 'DEFAULT', 'UNIQUE', 'AUTO_INCREMENT',
      'DESC', 'ASC'
    ];

    let highlighted = text;

    // Escape HTML
    highlighted = highlighted
      .replace(/&/g, '&amp;')
      .replace(/</g, '&lt;')
      .replace(/>/g, '&gt;');

    // Highlight SQL keywords (case insensitive)
    keywords.forEach(keyword => {
      const regex = new RegExp(`\\b(${keyword})\\b`, 'gi');
      highlighted = highlighted.replace(regex, '<span style="color: #569cd6; font-weight: bold;">$1</span>');
    });

    // Highlight strings
    highlighted = highlighted.replace(/'([^']*)'/g, '<span style="color: #ce9178;">\'$1\'</span>');
    highlighted = highlighted.replace(/"([^"]*)"/g, '<span style="color: #ce9178;">"$1"</span>');

    // Highlight numbers
    highlighted = highlighted.replace(/\b(\d+\.?\d*)\b/g, '<span style="color: #b5cea8;">$1</span>');

    // Highlight comments
    highlighted = highlighted.replace(/--.*$/gm, '<span style="color: #6a9955; font-style: italic;">$&</span>');
    highlighted = highlighted.replace(/\/\*[\s\S]*?\*\//g, '<span style="color: #6a9955; font-style: italic;">$&</span>');

    return highlighted;
  };

  useEffect(() => {
    if (highlightRef.current && textareaRef.current) {
      highlightRef.current.innerHTML = highlightSQL(value || '');

      // Sync scroll between textarea and highlight
      const syncScroll = (e) => {
        if (highlightRef.current) {
          highlightRef.current.scrollTop = e.target.scrollTop;
          highlightRef.current.scrollLeft = e.target.scrollLeft;
        }
      };

      textareaRef.current.addEventListener('scroll', syncScroll);

      return () => {
        if (textareaRef.current) {
          textareaRef.current.removeEventListener('scroll', syncScroll);
        }
      };
    }
  }, [value]);

  const containerStyle = {
    position: 'relative',
    width: '100%',
    height: height,
    backgroundColor: '#1e1e1e',
    fontFamily: 'Monaco, Menlo, "Ubuntu Mono", monospace',
    fontSize: '14px',
    lineHeight: '1.5',
    borderRadius: '4px',
    overflow: 'hidden',
    boxSizing: 'border-box'
  };

  const baseStyle = {
    position: 'absolute',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    padding: '16px',
    margin: 0,
    border: 'none',
    outline: 'none',
    resize: 'none',
    fontFamily: 'Monaco, Menlo, "Ubuntu Mono", monospace',
    fontSize: '14px',
    lineHeight: '1.5',
    whiteSpace: 'pre',
    tabSize: 2,
    boxSizing: 'border-box'
  };

  const textareaStyle = {
    ...baseStyle,
    backgroundColor: 'transparent',
    color: 'transparent',
    caretColor: 'transparent',
    zIndex: 2,
    overflow: 'auto'
  };

  const highlightStyle = {
    ...baseStyle,
    backgroundColor: '#1e1e1e',
    color: '#d4d4d4',
    zIndex: 1,
    pointerEvents: 'none',
    overflow: 'hidden'
  };

  return (
    <div ref={containerRef} style={containerStyle} className={className}>
      {/* Syntax highlighting layer */}
      <div 
        ref={highlightRef}
        style={highlightStyle}
      />

      {/* Invisible textarea for scrolling */}
      <textarea
        ref={textareaRef}
        value={value || ''}
        readOnly
        style={textareaStyle}
        tabIndex={-1}
      />
    </div>
  );
};

export default SQLHighlightDisplay;
