import React from 'react';

const SQLHighlightDisplay = ({ 
  value, 
  height = 'auto',
  className = ''
}) => {
  // Simple approach: just use a styled textarea with proper scrolling
  const containerStyle = {
    width: '100%',
    height: height,
    backgroundColor: '#1e1e1e',
    borderRadius: '4px',
    overflow: 'hidden',
    boxSizing: 'border-box'
  };

  const textareaStyle = {
    width: '100%',
    height: '100%',
    padding: '16px',
    margin: 0,
    border: 'none',
    outline: 'none',
    resize: 'none',
    fontFamily: 'Monaco, Menlo, "Ubuntu Mono", monospace',
    fontSize: '14px',
    lineHeight: '1.5',
    backgroundColor: '#1e1e1e',
    color: '#d4d4d4',
    whiteSpace: 'pre',
    overflow: 'auto',
    boxSizing: 'border-box',
    tabSize: 2
  };

  return (
    <div style={containerStyle} className={className}>
      <textarea
        value={value || ''}
        readOnly
        style={textareaStyle}
        tabIndex={-1}
      />
    </div>
  );
};

export default SQLHighlightDisplay;
