import React, { useState, useEffect } from 'react';
import axios from 'axios';

const RecommendationDashboard = () => {
  const [recommendations, setRecommendations] = useState([]);
  const [dailyChallenge, setDailyChallenge] = useState(null);
  const [userProfile, setUserProfile] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchRecommendations();
    fetchDailyChallenge();
  }, []);

  const fetchRecommendations = async () => {
    try {
      const sessionId = localStorage.getItem('sessionId') || 'anonymous-' + Date.now();
      const response = await axios.get('http://localhost:5001/api/recommendations/problems', {
        headers: { 'x-session-id': sessionId }
      });
      if (response.data.success) {
        setRecommendations(response.data.data.recommendations);
        setUserProfile(response.data.data.userProfile);
      }
    } catch (error) {
      console.error('Error fetching recommendations:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchDailyChallenge = async () => {
    try {
      const sessionId = localStorage.getItem('sessionId') || 'anonymous-' + Date.now();
      const response = await axios.get('http://localhost:5001/api/recommendations/daily-challenge', {
        headers: { 'x-session-id': sessionId }
      });
      if (response.data.success) {
        setDailyChallenge(response.data.data.challenge);
      }
    } catch (error) {
      console.error('Error fetching daily challenge:', error);
    }
  };

  const getDifficultyColor = (difficulty) => {
    switch (difficulty) {
      case 'easy': return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200';
      case 'medium': return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200';
      case 'hard': return 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200';
      default: return 'bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200';
    }
  };

  const getSkillLevelEmoji = (level) => {
    const levels = {
      'Newcomer': 'ðŸŒ±',
      'Beginner': 'ðŸ“š',
      'Intermediate': 'ðŸ’ª',
      'Advanced': 'ðŸš€',
      'Expert': 'ðŸ‘‘'
    };
    return levels[level] || 'ðŸŒ±';
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center p-8">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header Section */}
      <div className="bg-gradient-to-r from-blue-600 to-purple-600 rounded-2xl p-6 text-white">
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-2xl font-bold mb-2">Your SQL Learning Journey</h2>
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2">
                <span className="text-2xl">{getSkillLevelEmoji(userProfile?.skill_level)}</span>
                <span className="text-lg font-semibold">{userProfile?.skill_level || 'Newcomer'}</span>
              </div>
              <div className="h-6 w-px bg-white/30"></div>
              <div className="text-sm opacity-90">
                {userProfile?.completed_problems || 0} problems solved
              </div>
            </div>
          </div>
          <div className="text-right">
            <div className="text-3xl font-bold">{userProfile?.success_rate || 0}%</div>
            <div className="text-sm opacity-90">Success Rate</div>
          </div>
        </div>
      </div>

      {/* Daily Challenge Section */}
      {dailyChallenge && (
        <div className="bg-gradient-to-r from-orange-400 to-pink-500 rounded-2xl p-6 text-white shadow-lg transform hover:scale-[1.02] transition-all duration-300">
          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center space-x-3">
              <div className="text-3xl">ðŸ”¥</div>
              <div>
                <h3 className="text-xl font-bold">Daily Challenge</h3>
                <p className="text-sm opacity-90">Keep your streak alive!</p>
              </div>
            </div>
            <div className="text-right">
              <div className="text-2xl font-bold">#{dailyChallenge.numeric_id}</div>
              <span className={`px-3 py-1 rounded-full text-xs font-medium ${getDifficultyColor(dailyChallenge.difficulty)}`}>
                {dailyChallenge.difficulty}
              </span>
            </div>
          </div>
          <h4 className="text-lg font-semibold mb-2">{dailyChallenge.title}</h4>
          <p className="text-sm opacity-90 line-clamp-2">{dailyChallenge.description.substring(0, 120)}...</p>
          <button 
            className="mt-4 bg-white text-orange-500 px-6 py-2 rounded-lg font-semibold hover:bg-orange-50 transition-colors duration-200"
            onClick={() => window.location.href = `/practice/${dailyChallenge.id}`}
          >
            Start Challenge
          </button>
        </div>
      )}

      {/* Recommendations Section */}
      <div>
        <div className="flex items-center justify-between mb-6">
          <h3 className="text-2xl font-bold text-gray-900 dark:text-white">
            ðŸ“š Recommended for You
          </h3>
          <span className="text-sm text-gray-500 dark:text-gray-400">
            Personalized based on your progress
          </span>
        </div>

        <div className="grid gap-4">
          {recommendations.map((problem, index) => (
            <div 
              key={problem.id}
              className="group relative bg-white dark:bg-gray-800 rounded-xl border border-gray-200 dark:border-gray-700 hover:border-blue-300 dark:hover:border-blue-600 hover:shadow-lg transition-all duration-300 transform hover:-translate-y-1"
            >
              {/* Recommendation Score Badge */}
              <div className="absolute -top-2 -right-2 z-10">
                <div className="bg-gradient-to-r from-blue-500 to-purple-500 text-white text-xs font-bold px-2 py-1 rounded-full shadow-lg">
                  {problem.recommendation_score}% match
                </div>
              </div>

              <div className="p-6">
                <div className="flex items-start justify-between mb-4">
                  <div className="flex-1">
                    <div className="flex items-center space-x-3 mb-2">
                      <span className="text-2xl font-bold text-blue-600 dark:text-blue-400">
                        #{problem.numeric_id}
                      </span>
                      <span className={`px-3 py-1 rounded-full text-xs font-medium ${getDifficultyColor(problem.difficulty)}`}>
                        {problem.difficulty}
                      </span>
                      <span className="px-3 py-1 bg-blue-50 text-blue-700 dark:bg-blue-900 dark:text-blue-200 rounded-full text-xs font-medium">
                        {problem.category}
                      </span>
                    </div>
                    <h4 className="text-lg font-semibold text-gray-900 dark:text-white mb-2 group-hover:text-blue-600 dark:group-hover:text-blue-400 transition-colors">
                      {problem.title}
                    </h4>
                    <p className="text-sm text-gray-600 dark:text-gray-300 line-clamp-2 mb-3">
                      {problem.description.substring(0, 150)}...
                    </p>
                  </div>
                </div>

                {/* Recommendation Reason */}
                <div className="mb-4">
                  <div className="flex items-center space-x-2 text-sm">
                    <div className="w-2 h-2 bg-blue-500 rounded-full"></div>
                    <span className="text-blue-600 dark:text-blue-400 font-medium">
                      {problem.recommendation_reason}
                    </span>
                  </div>
                </div>

                {/* Hints Available */}
                {problem.hint_count > 0 && (
                  <div className="flex items-center space-x-2 text-sm text-gray-500 dark:text-gray-400 mb-4">
                    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9.663 17h4.673M12 3v1m6.364 1.636l-.707.707M21 12h-1M4 12H3m3.343-5.657l-.707-.707m2.828 9.9a5 5 0 117.072 0l-.548.547A3.374 3.374 0 0014 18.469V19a2 2 0 11-4 0v-.531c0-.895-.356-1.754-.988-2.386l-.548-.547z" />
                    </svg>
                    <span>{problem.hint_count} hints available</span>
                  </div>
                )}

                {/* Action Button */}
                <button 
                  className="w-full bg-gradient-to-r from-blue-500 to-purple-500 hover:from-blue-600 hover:to-purple-600 text-white font-semibold py-3 px-6 rounded-lg transition-all duration-300 transform hover:scale-[1.02] shadow-md hover:shadow-lg"
                  onClick={() => window.location.href = `/practice/${problem.id}`}
                >
                  Start Problem
                </button>
              </div>

              {/* Hover Effect Overlay */}
              <div className="absolute inset-0 bg-gradient-to-r from-blue-500/5 to-purple-500/5 rounded-xl opacity-0 group-hover:opacity-100 transition-opacity duration-300 pointer-events-none"></div>
            </div>
          ))}
        </div>

        {recommendations.length === 0 && (
          <div className="text-center py-12">
            <div className="text-6xl mb-4">ðŸŽ¯</div>
            <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-2">
              Ready to start your SQL journey?
            </h3>
            <p className="text-gray-600 dark:text-gray-400 mb-6">
              Complete a few problems to get personalized recommendations!
            </p>
            <button 
              className="bg-blue-500 hover:bg-blue-600 text-white font-semibold py-3 px-6 rounded-lg transition-colors"
              onClick={() => window.location.href = '/problems'}
            >
              Browse All Problems
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default RecommendationDashboard;
