import React, { useState, useEffect } from 'react';

const BookmarkButton = ({ problemId, size = 'md', className = '' }) => {
  const [isBookmarked, setIsBookmarked] = useState(false);
  const [bookmarkType, setBookmarkType] = useState(null);
  const [showDropdown, setShowDropdown] = useState(false);
  const [loading, setLoading] = useState(false);

  // Get session ID (same logic as other components)
  const getSessionId = () => {
    let sessionId = localStorage.getItem('sql_practice_session_id');
    if (!sessionId) {
      sessionId = `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
      localStorage.setItem('sql_practice_session_id', sessionId);
    }
    return sessionId;
  };

  // Check if problem is already bookmarked when component loads
  useEffect(() => {
    checkBookmarkStatus();
  }, [problemId]);

  const checkBookmarkStatus = async () => {
    try {
      const sessionId = getSessionId();
      const response = await fetch(`http://localhost:5001/api/bookmarks/check/${problemId}`, {
        headers: {
          'X-Session-ID': sessionId
        }
      });

      if (response.ok) {
        const data = await response.json();
        setIsBookmarked(data.bookmarked);
        setBookmarkType(data.bookmarkType);
      }
    } catch (error) {
      console.error('Error checking bookmark status:', error);
    }
  };

  const handleBookmark = async (type) => {
    if (loading) return;

    setLoading(true);
    try {
      const sessionId = getSessionId();

      if (isBookmarked) {
        // Remove bookmark
        const response = await fetch(`http://localhost:5001/api/bookmarks/${problemId}`, {
          method: 'DELETE',
          headers: {
            'X-Session-ID': sessionId
          }
        });

        if (response.ok) {
          setIsBookmarked(false);
          setBookmarkType(null);
        }
      } else {
        // Add bookmark
        const response = await fetch(`http://localhost:5001/api/bookmarks/${problemId}`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'X-Session-ID': sessionId
          },
          body: JSON.stringify({
            bookmarkType: type
          })
        });

        if (response.ok) {
          setIsBookmarked(true);
          setBookmarkType(type);
        }
      }
    } catch (error) {
      console.error('Error updating bookmark:', error);
    } finally {
      setLoading(false);
      setShowDropdown(false);
    }
  };

  const getSize = () => {
    switch (size) {
      case 'sm': return 'w-8 h-8 text-sm';
      case 'md': return 'w-10 h-10 text-base';
      case 'lg': return 'w-12 h-12 text-lg';
      default: return 'w-10 h-10 text-base';
    }
  };

  const getBookmarkIcon = () => {
    if (!isBookmarked) return '🔖';
    switch (bookmarkType) {
      case 'favorite': return '⭐';
      case 'review': return '📚';
      case 'challenging': return '🔥';
      default: return '⭐';
    }
  };

  const toggleBookmark = () => {
    if (isBookmarked) {
      // If already bookmarked, remove it
      handleBookmark(null);
    } else {
      // If not bookmarked, show dropdown
      setShowDropdown(!showDropdown);
    }
  };

  return (
    <div className={`relative ${className}`}>
      <button
        onClick={toggleBookmark}
        disabled={loading}
        className={`
          ${getSize()}
          flex items-center justify-center
          rounded-full border-2 transition-all duration-200
          ${loading 
            ? 'bg-gray-100 border-gray-300 text-gray-400 cursor-not-allowed'
            : isBookmarked 
              ? 'bg-blue-100 border-blue-500 text-blue-600' 
              : 'bg-gray-100 border-gray-300 text-gray-500 hover:bg-gray-200'
          }
          ${!loading && 'hover:scale-105'}
        `}
        title={isBookmarked ? `Remove ${bookmarkType} bookmark` : "Bookmark this problem"}
      >
        {loading ? '⏳' : getBookmarkIcon()}
      </button>

      {showDropdown && !isBookmarked && (
        <div className="absolute top-full right-0 mt-2 w-48 bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-600 rounded-lg shadow-lg z-50">
          <div className="p-2">
            <button
              onClick={() => handleBookmark('favorite')}
              disabled={loading}
              className="w-full text-left px-3 py-2 rounded-md text-sm hover:bg-gray-100 dark:hover:bg-gray-700 text-gray-700 dark:text-gray-300"
            >
              ⭐ Favorite
            </button>
            <button
              onClick={() => handleBookmark('review')}
              disabled={loading}
              className="w-full text-left px-3 py-2 rounded-md text-sm hover:bg-gray-100 dark:hover:bg-gray-700 text-gray-700 dark:text-gray-300"
            >
              📚 Review Later
            </button>
            <button
              onClick={() => handleBookmark('challenging')}
              disabled={loading}
              className="w-full text-left px-3 py-2 rounded-md text-sm hover:bg-gray-100 dark:hover:bg-gray-700 text-gray-700 dark:text-gray-300"
            >
              🔥 Challenging
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default BookmarkButton;
