import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import ProgressService from '../services/progressService';

function ProgressPage() {
  const [problems, setProblems] = useState([]);
  const [companies, setCompanies] = useState([]);
  const [userProgress, setUserProgress] = useState([]);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({
    totalProblems: 0,
    easyProblems: 0,
    mediumProblems: 0,
    hardProblems: 0,
    companiesCount: 0,
    averageAcceptanceRate: 0,
    solvedProblems: 0,
    correctAttempts: 0,
    totalAttempts: 0
  });

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        // Fetch problems
        const problemsResponse = await fetch('http://localhost:5001/api/sql/problems');
        const problemsData = await problemsResponse.json();

        // Fetch companies
        const companiesResponse = await fetch('http://localhost:5001/api/sql/companies');
        const companiesData = await companiesResponse.json();

        // Fetch user progress
        let progressData = [];
        let progressStats = {
          solvedProblems: 0,
          correctAttempts: 0,
          totalAttempts: 0
        };
        try {
          console.log('DEBUG: Fetching progress with session ID:', ProgressService.sessionId);
          const progressOverview = await ProgressService.getProgressOverview();
          console.log('DEBUG: Progress overview response:', progressOverview);

          if (progressOverview && progressOverview.overview) {
            progressStats.solvedProblems = progressOverview.overview.completed || 0;
            progressStats.totalAttempts = progressOverview.overview.attempted || 0;
            progressStats.correctAttempts = progressOverview.overview.completed || 0;
            console.log('DEBUG: Extracted progress stats:', progressStats);
          }

          // Get detailed progress for individual problems
          const detailedProgress = await ProgressService.getDetailedProgress();
          console.log('DEBUG: Detailed progress response:', detailedProgress);
          progressData = detailedProgress || [];
          setUserProgress(progressData);
        } catch (error) {
          console.log('Progress data not available:', error);
          setUserProgress([]);
        }

        if (problemsResponse.ok) {
          setProblems(problemsData.problems);

          // Calculate real statistics
          const totalProblems = problemsData.problems.length;
          const easyProblems = problemsData.problems.filter(p => p.difficulty === 'easy').length;
          const mediumProblems = problemsData.problems.filter(p => p.difficulty === 'medium').length;
          const hardProblems = problemsData.problems.filter(p => p.difficulty === 'hard').length;
          const companiesCount = companiesData.companies?.length || 0;
          const averageAcceptanceRate = Math.round(
            problemsData.problems.reduce((sum, p) => sum + p.acceptance_rate, 0) / totalProblems
          );

          // Calculate user progress stats - use API data if available, otherwise calculate from detailed progress
          let solvedProblems = progressStats.solvedProblems;
          let totalAttempts = progressStats.totalAttempts;
          let correctAttempts = progressStats.correctAttempts;

          // If we have detailed progress data, recalculate for accuracy
          if (progressData.length > 0) {
            solvedProblems = progressData.filter(p => p.status === 'completed').length;
            totalAttempts = progressData.reduce((sum, p) => sum + (p.total_attempts || 0), 0);
            correctAttempts = progressData.reduce((sum, p) => sum + (p.correct_attempts || 0), 0);
          }

          setStats({
            totalProblems,
            easyProblems,
            mediumProblems,
            hardProblems,
            companiesCount,
            averageAcceptanceRate,
            solvedProblems,
            correctAttempts,
            totalAttempts
          });

          setCompanies(companiesData.companies || []);
        }
      } catch (error) {
        console.error('Failed to fetch progress data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto px-4 py-8">
        <div className="text-center py-12">
          <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
          <div className="text-gray-500 mt-2">Loading progress data...</div>
        </div>
      </div>
    );
  }

  const getCategoryProgress = () => {
    const categories = {};
    problems.forEach(problem => {
      const category = problem.category || 'Uncategorized';
      if (!categories[category]) {
        categories[category] = { total: 0, avgAcceptance: 0, acceptance: [] };
      }
      categories[category].total++;
      categories[category].acceptance.push(problem.acceptance_rate);
    });

    return Object.entries(categories).map(([name, data]) => ({
      skill: name,
      problems: data.total,
      progress: Math.round(data.acceptance.reduce((sum, rate) => sum + rate, 0) / data.total)
    })).sort((a, b) => b.problems - a.problems);
  };

  const getTopCompanies = () => {
    return companies
      .sort((a, b) => b.count - a.count)
      .slice(0, 10);
  };

  return (
    <div className="max-w-7xl mx-auto px-4 py-8 animate-fade-in">
      <div className="mb-8">
        <h1 className="text-4xl font-bold text-gray-900 mb-2 bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
          Platform Analytics
        </h1>
        <p className="text-gray-600">Real-time insights into the SQL Practice Platform</p>
      </div>

      {/* Statistics Cards */}
      <div className="grid grid-cols-1 md:grid-cols-5 gap-6 mb-8">
        <div className="bg-gradient-to-br from-blue-500 to-blue-600 text-white rounded-xl shadow-lg p-6 text-center hover-lift">
          <div className="text-3xl font-bold mb-2">
            {stats.totalProblems}
          </div>
          <div className="text-blue-100">Total Problems</div>
        </div>

        <div className="bg-gradient-to-br from-green-500 to-green-600 text-white rounded-xl shadow-lg p-6 text-center hover-lift">
          <div className="text-3xl font-bold mb-2">
            {stats.easyProblems}
          </div>
          <div className="text-green-100">Easy Problems</div>
        </div>

        <div className="bg-gradient-to-br from-yellow-500 to-yellow-600 text-white rounded-xl shadow-lg p-6 text-center hover-lift">
          <div className="text-3xl font-bold mb-2">
            {stats.mediumProblems}
          </div>
          <div className="text-yellow-100">Medium Problems</div>
        </div>

        <div className="bg-gradient-to-br from-red-500 to-red-600 text-white rounded-xl shadow-lg p-6 text-center hover-lift">
          <div className="text-3xl font-bold mb-2">
            {stats.hardProblems}
          </div>
          <div className="text-red-100">Hard Problems</div>
        </div>

        <div className="bg-gradient-to-br from-purple-500 to-purple-600 text-white rounded-xl shadow-lg p-6 text-center hover-lift">
          <div className="text-3xl font-bold mb-2">
            {stats.companiesCount}
          </div>
          <div className="text-purple-100">Companies</div>
        </div>
      </div>

      {/* User Progress Cards */}
      <div className="mb-8">
        <h2 className="text-2xl font-bold text-gray-900 mb-4">Your Progress</h2>
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
          <div className="bg-gradient-to-br from-emerald-500 to-emerald-600 text-white rounded-xl shadow-lg p-6 text-center hover-lift">
            <div className="text-3xl font-bold mb-2">
              {stats.solvedProblems}
            </div>
            <div className="text-emerald-100">Problems Solved</div>
            <div className="text-xs text-emerald-200 mt-1">
              {stats.totalProblems > 0 ? Math.round((stats.solvedProblems / stats.totalProblems) * 100) : 0}% Complete
            </div>
          </div>

          <div className="bg-gradient-to-br from-blue-500 to-blue-600 text-white rounded-xl shadow-lg p-6 text-center hover-lift">
            <div className="text-3xl font-bold mb-2">
              {stats.totalAttempts}
            </div>
            <div className="text-blue-100">Total Attempts</div>
            <div className="text-xs text-blue-200 mt-1">
              All queries executed
            </div>
          </div>

          <div className="bg-gradient-to-br from-green-500 to-green-600 text-white rounded-xl shadow-lg p-6 text-center hover-lift">
            <div className="text-3xl font-bold mb-2">
              {stats.correctAttempts}
            </div>
            <div className="text-green-100">Correct Solutions</div>
            <div className="text-xs text-green-200 mt-1">
              Successful queries
            </div>
          </div>

          <div className="bg-gradient-to-br from-indigo-500 to-indigo-600 text-white rounded-xl shadow-lg p-6 text-center hover-lift">
            <div className="text-3xl font-bold mb-2">
              {stats.totalAttempts > 0 ? Math.round((stats.correctAttempts / stats.totalAttempts) * 100) : 0}%
            </div>
            <div className="text-indigo-100">Success Rate</div>
            <div className="text-xs text-indigo-200 mt-1">
              Accuracy percentage
            </div>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Category Distribution */}
        <div className="bg-white rounded-xl shadow-lg card-hover-effect">
          <div className="border-b border-gray-200 px-6 py-4">
            <h2 className="text-xl font-semibold text-gray-900">📚 Category Distribution</h2>
          </div>
          <div className="p-6">
            <div className="space-y-6">
              {getCategoryProgress().map((category, index) => (
                <div key={index} className="animate-slide-up" style={{animationDelay: `${index * 100}ms`}}>
                  <div className="flex justify-between items-center mb-2">
                    <span className="text-sm font-medium text-gray-700">{category.skill}</span>
                    <span className="text-sm text-gray-500">{category.problems} problems</span>
                  </div>
                  <div className="w-full bg-gray-200 rounded-full h-3">
                    <div 
                      className="bg-gradient-to-r from-blue-500 to-purple-600 h-3 rounded-full transition-all duration-1000 animate-progress" 
                      style={{ width: `${Math.min(category.progress, 100)}%` }}
                    ></div>
                  </div>
                  <div className="text-right text-xs text-gray-500 mt-1">
                    {category.progress}% avg acceptance
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Top Companies */}
        <div className="bg-white rounded-xl shadow-lg card-hover-effect">
          <div className="border-b border-gray-200 px-6 py-4">
            <h2 className="text-xl font-semibold text-gray-900">🏢 Top Companies</h2>
          </div>
          <div className="p-6">
            <div className="space-y-4">
              {getTopCompanies().map((company, index) => (
                <div key={company.name} className="flex items-center justify-between p-3 rounded-lg hover:bg-gray-50 transition-colors animate-slide-up" style={{animationDelay: `${index * 50}ms`}}>
                  <div className="flex items-center space-x-3">
                    <div className="w-8 h-8 bg-blue-100 rounded-full flex items-center justify-center">
                      <span className="text-sm font-bold text-blue-600">#{index + 1}</span>
                    </div>
                    <div>
                      <div className="font-medium text-gray-900">{company.name}</div>
                      <div className="text-sm text-gray-500">{company.count} problems</div>
                    </div>
                  </div>
                  <div className="flex items-center space-x-2">
                    <div className="w-20 bg-gray-200 rounded-full h-2">
                      <div 
                        className="bg-blue-600 h-2 rounded-full" 
                        style={{ width: `${(company.count / Math.max(...companies.map(c => c.count))) * 100}%` }}
                      ></div>
                    </div>
                    <Link
                      to={`/problems?company=${encodeURIComponent(company.name)}`}
                      className="text-blue-600 hover:text-blue-800 text-sm font-medium"
                    >
                      View →
                    </Link>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* Platform Insights */}
      <div className="mt-8 bg-white rounded-xl shadow-lg card-hover-effect">
        <div className="border-b border-gray-200 px-6 py-4">
          <h2 className="text-xl font-semibold text-gray-900">📊 Platform Insights</h2>
        </div>
        <div className="p-6">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="flex items-center space-x-4 p-6 bg-gradient-to-br from-green-50 to-green-100 rounded-xl border border-green-200">
              <div className="text-3xl">🎯</div>
              <div>
                <div className="text-lg font-bold text-green-800">100% Success Rate</div>
                <div className="text-sm text-green-600">All {stats.totalProblems} problems are fully functional</div>
              </div>
            </div>

            <div className="flex items-center space-x-4 p-6 bg-gradient-to-br from-blue-50 to-blue-100 rounded-xl border border-blue-200">
              <div className="text-3xl">🏢</div>
              <div>
                <div className="text-lg font-bold text-blue-800">Fortune 100 Focus</div>
                <div className="text-sm text-blue-600">Problems from {stats.companiesCount} real companies</div>
              </div>
            </div>

            <div className="flex items-center space-x-4 p-6 bg-gradient-to-br from-purple-50 to-purple-100 rounded-xl border border-purple-200">
              <div className="text-3xl">⚖️</div>
              <div>
                <div className="text-lg font-bold text-purple-800">Balanced Difficulty</div>
                <div className="text-sm text-purple-600">
                  {stats.easyProblems}E / {stats.mediumProblems}M / {stats.hardProblems}H
                </div>
              </div>
            </div>
          </div>

          {/* Quick Actions */}
          <div className="mt-8 pt-6 border-t border-gray-200">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">🚀 Quick Actions</h3>
            <div className="flex flex-wrap gap-4">
              <Link
                to="/problems?difficulty=easy"
                className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-lg transition-colors hover-lift btn-press"
              >
                Start with Easy Problems
              </Link>
              <Link
                to="/problems?difficulty=medium"
                className="bg-yellow-500 hover:bg-yellow-600 text-white px-4 py-2 rounded-lg transition-colors hover-lift btn-press"
              >
                Try Medium Challenges
              </Link>
              <Link
                to="/problems?difficulty=hard"
                className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-lg transition-colors hover-lift btn-press"
              >
                Master Hard Problems
              </Link>
              <Link
                to="/practice"
                className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-lg transition-colors hover-lift btn-press"
              >
                Open SQL Editor
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ProgressPage;
